  `parent_id` int(11) NOT NULL DEFAULT '-1',
  `caption_save` varchar(25) NOT NULL DEFAULT '',
  `caption` varchar(128) NOT NULL,
  `icon_color` int(5) NOT NULL DEFAULT '1',
  `icon_image` int(5) NOT NULL DEFAULT '1',
  `visible` enum('0','1') NOT NULL DEFAULT '1',
  `enabled` enum('0','1') NOT NULL DEFAULT '1',
  `min_rank` int(2) NOT NULL DEFAULT '1',
  `club_only` enum('0','1') NOT NULL DEFAULT '0',
  `order_num` int(5) NOT NULL DEFAULT '1',
  `page_layout` enum('default_3x3','club_buy','club_gift','frontpage','spaces','recycler','recycler_info','recycler_prizes','trophies','plasto','marketplace','marketplace_own_items','pets','spaces_new','soundmachine','guilds','guild_furni','info_duckets','info_rentables','info_pets','roomads','single_bundle','sold_ltd_items','badge_display','bots','pets2','pets3','productpage1','room_bundle','recent_purchases','pets2','pets3','default_3x3_color_grouping','guild_forum','vip_buy','info_loyalty','loyalty_vip_buy','collectibles','frontpage_featured') NOT NULL DEFAULT 'default_3x3',
  `page_headline` varchar(1024) NOT NULL DEFAULT '',
  `page_teaser` varchar(64) NOT NULL DEFAULT '',
  `page_special` varchar(2048) NOT NULL DEFAULT '',
  `page_text1` text NOT NULL,
  `page_text2` text NOT NULL,
  `page_text_details` text NOT NULL,
  `page_text_teaser` text NOT NULL,
  `vip_only` enum('1','0') NOT NULL DEFAULT '0',
  `includes` varchar(32) NOT NULL DEFAULT '' COMMENT 'Example usage: 1;2;3\n This will include page 1, 2 and 3 in the current page.\n Note that permissions are only used for the current entry.',
  `room_id` int(11) NOT NULL DEFAULT '0'
) ENGINE=MyISAM DEFAULT CHARSET=latin1;

